(129, 'theme_mods_twentyfourteen-child', 'a:36:{i:0;b:0;s:18:"nav_menu_locations";a:2:{s:7:"primary";i:2;s:9:"secondary";i:2;}s:16:"header_textcolor";s:6:"3a3a3a";s:16:"background_color";s:6:"ffffff";s:16:"background_image";s:0:"";s:17:"background_repeat";s:6:"repeat";s:21:"background_position_x";s:4:"left";s:21:"background_attachment";s:5:"fixed";s:23:"featured_content_layout";s:4:"grid";s:22:"fourteenxt_center_site";b:0;s:29:"fourteenxt_primary_menu_float";s:5:"right";s:28:"fourteenxt_hide_left_sidebar";s:0:"";s:32:"fourteenxt_overall_content_width";s:4:"1038";s:30:"fourteenxt_fullwidth_blog_feed";s:0:"";s:32:"fourteenxt_fullwidth_single_post";b:1;s:29:"fourteenxt_fullwidth_archives";b:1;s:29:"fourteenxt_fullwidth_searches";b:1;s:37:"fourteenxt_content_off_featured_image";s:0:"";s:35:"fourteenxt_content_width_adjustment";s:3:"874";s:19:"fourteenxt_feed_cat";s:0:"";s:24:"fourteenxt_home_excerpts";s:0:"";s:25:"fourteenxt_excerpt_length";i:55;s:25:"fourteenxt_fitvids_enable";s:0:"";s:27:"fourteenxt_fitvids_selector";s:5:".post";s:34:"fourteenxt_fitvids_custom_selector";s:0:"";s:30:"fourteenxt_featured_visibility";s:0:"";s:27:"fourteenxt_enable_autoslide";s:0:"";s:23:"fourteenxt_slider_width";s:4:"1600";s:24:"fourteenxt_slider_height";s:3:"500";s:27:"fourteenxt_slider_topmargin";s:1:"0";s:33:"fourteenxt_featured_bg_visibility";s:0:"";s:12:"header_image";s:79:"http://localhost:8888/wordpress/wp-content/uploads/2014/01/cropped-header21.jpg";s:17:"header_image_data";a:5:{s:13:"attachment_id";i:37;s:3:"url";s:79:"http://localhost:8888/wordpress/wp-content/uploads/2014/01/cropped-header21.jpg";s:13:"thumbnail_url";s:79:"http://localhost:8888/wordpress/wp-content/uploads/2014/01/cropped-header21.jpg";s:5:"width";i:1260;s:6:"height";i:240;}s:12:"accent_color";s:7:"#ff1414";s:19:"fourteen_colors_css";s:8018:"/* Custom Contrast Color */\r\n		.site:before,\r\n		#secondary,\r\n		.site-header,\r\n		.site-footer,\r\n		.featured-content,\r\n		.featured-content .entry-header,\r\n		.slider-direction-nav a {\r\n			background-color: #cccccc;\r\n		}\r\n		\r\n		.grid .featured-content .entry-header {\r\n			border-color: #cccccc;\r\n		}\r\n		\r\n		.slider-control-paging a:before {\r\n			background-color: rgba(255,255,255,.33);\r\n		}\r\n		\r\n		.hentry .mejs-mediaelement,\r\n		.hentry .mejs-container .mejs-controls {\r\n			background: #cccccc;\r\n		}\r\n	\r\n			.site-description,\r\n			.secondary-navigation a,\r\n			.widget,\r\n			.widget a,\r\n			.widget_calendar caption,\r\n			.site-header a,\r\n			.site-title a,\r\n			.site-title a:hover,\r\n			.menu-toggle:before,\r\n			.site-footer,\r\n			.site-footer a,\r\n			.featured-content a,\r\n			.featured-content .entry-meta,\r\n			.slider-direction-nav a:before,\r\n			.hentry .mejs-container .mejs-controls .mejs-time span,\r\n			.hentry .mejs-controls .mejs-button button {\r\n				color: #2b2b2b;\r\n			}\r\n\r\n			.primary-navigation ul ul a,\r\n			.secondary-navigation ul ul a,\r\n			.secondary-navigation li:hover > a,\r\n			.secondary-navigation li.focus > a,\r\n			.widget_calendar tbody a,\r\n			.site-footer .widget_calendar tbody a,\r\n			.slider-direction-nav a:hover:before {\r\n				color: #fff;\r\n			}\r\n\r\n			.slider-control-paging a:before {\r\n				background-color: rgba(0, 0, 0, .33);\r\n			}\r\n			\r\n			.featured-content {\r\n				background-image: url(http://localhost:8888/wordpress/wp-content/plugins/fourteen-colors/pattern-dark-inverse.svg);\r\n			}\r\n\r\n			.widget-title, .widget-title a {\r\n				color: #000;\r\n			}\r\n\r\n			.secondary-navigation li {\r\n				border-color: rgba(0, 0, 0, .2);\r\n			}\r\n\r\n			.secondary-navigation {\r\n				border-color: rgba(0, 0, 0, .2);\r\n			}\r\n\r\n			.widget input,\r\n			.widget textarea {\r\n				background-color: rgba(0, 0, 0, .01);\r\n				border-color: rgba(0, 0, 0, .2);\r\n				color: #000;\r\n			}\r\n\r\n			.widget input:focus, .widget textarea:focus {\r\n				border-color: rgba(0, 0, 0, 0.4);\r\n			}\r\n\r\n			#supplementary + .site-info {\r\n				border-top: 1px solid rgba(0, 0, 0, 0.2);\r\n			}\r\n			\r\n			.hentry .mejs-controls .mejs-time-rail .mejs-time-total, \r\n			.hentry .mejs-controls .mejs-horizontal-volume-slider .mejs-horizontal-volume-total {\r\n				background: rgba(0,0,0,.3);\r\n			}\r\n			\r\n			.mejs-overlay .mejs-overlay-button {\r\n				background-color: #cccccc;\r\n			}\r\n\r\n			.hentry .mejs-controls .mejs-horizontal-volume-slider .mejs-horizontal-volume-current {\r\n				background-color: #2b2b2b;\r\n			}\r\n		\r\n		/* Custom accent color. */\r\n		button,\r\n		.contributor-posts-link,\r\n		input[type="button"],\r\n		input[type="reset"],\r\n		input[type="submit"],\r\n		.search-toggle,\r\n		.hentry .mejs-controls .mejs-time-rail .mejs-time-current,\r\n		.mejs-overlay:hover .mejs-overlay-button,\r\n		.widget button,\r\n		.widget input[type="button"],\r\n		.widget input[type="reset"],\r\n		.widget input[type="submit"],\r\n		.widget_calendar tbody a,\r\n		.content-sidebar .widget input[type="button"],\r\n		.content-sidebar .widget input[type="reset"],\r\n		.content-sidebar .widget input[type="submit"],\r\n		.slider-control-paging .slider-active:before,\r\n		.slider-control-paging .slider-active:hover:before,\r\n		.slider-direction-nav a:hover {\r\n			background-color: #ff1414;\r\n		}\r\n\r\n		.site-navigation a:hover {\r\n			color: #ff1414;\r\n		}\r\n\r\n		::-moz-selection {\r\n			background: #ff1414;\r\n		}\r\n\r\n		::selection {\r\n			background: #ff1414;\r\n		}\r\n\r\n		.paging-navigation .page-numbers.current {\r\n			border-color: #ff1414;\r\n		}\r\n\r\n		@media screen and (min-width: 782px) {\r\n			.primary-navigation li:hover > a,\r\n			.primary-navigation li.focus > a,\r\n			.primary-navigation ul ul {\r\n				background-color: #ff1414;\r\n			}\r\n		}\r\n\r\n		@media screen and (min-width: 1008px) {\r\n			.secondary-navigation li:hover > a,\r\n			.secondary-navigation li.focus > a,\r\n			.secondary-navigation ul ul {\r\n				background-color: #ff1414;\r\n			}\r\n		}\r\n	\r\n		/* Generated variants of custom accent color. */\r\n		a,\r\n		.content-sidebar .widget a {\r\n			color: #ff1414;\r\n		}\r\n		\r\n		.contributor-posts-link:hover,\r\n		.slider-control-paging a:hover:before,\r\n		.search-toggle:hover,\r\n		.search-toggle.active,\r\n		.search-box,\r\n		.widget_calendar tbody a:hover,\r\n		button:hover,\r\n		button:focus,\r\n		input[type="button"]:hover,\r\n		input[type="button"]:focus,\r\n		input[type="reset"]:hover,\r\n		input[type="reset"]:focus,\r\n		input[type="submit"]:hover,\r\n		input[type="submit"]:focus,\r\n		.widget input[type="button"]:hover,\r\n		.widget input[type="button"]:focus,\r\n		.widget input[type="reset"]:hover,\r\n		.widget input[type="reset"]:focus,\r\n		.widget input[type="submit"]:hover,\r\n		.widget input[type="submit"]:focus,\r\n		.content-sidebar .widget input[type="button"]:hover,\r\n		.content-sidebar .widget input[type="button"]:focus,\r\n		.content-sidebar .widget input[type="reset"]:hover,\r\n		.content-sidebar .widget input[type="reset"]:focus,\r\n		.content-sidebar .widget input[type="submit"]:hover,\r\n		.content-sidebar .widget input[type="submit"]:focus {\r\n			background-color: #ff3131;\r\n		}\r\n\r\n		.featured-content a:hover,\r\n		.featured-content .entry-title a:hover,\r\n		.widget a:hover,\r\n		.widget-title a:hover,\r\n		.widget_twentyfourteen_ephemera .entry-meta a:hover,\r\n		.hentry .mejs-controls .mejs-button button:hover,\r\n		.site-info a:hover,\r\n		.featured-content a:hover {\r\n			color: #ff3131;\r\n		}\r\n\r\n		a:active,\r\n		a:hover,\r\n		.entry-title a:hover,\r\n		.entry-meta a:hover,\r\n		.cat-links a:hover,\r\n		.entry-content .edit-link a:hover,\r\n		.post-navigation a:hover,\r\n		.image-navigation a:hover,\r\n		.comment-author a:hover,\r\n		.comment-list .pingback a:hover,\r\n		.comment-list .trackback a:hover,\r\n		.comment-metadata a:hover,\r\n		.comment-reply-title small a:hover,\r\n		.content-sidebar .widget a:hover,\r\n		.content-sidebar .widget .widget-title a:hover,\r\n		.content-sidebar .widget_twentyfourteen_ephemera .entry-meta a:hover {\r\n			color: #ff3131;\r\n		}\r\n\r\n		.page-links a:hover,\r\n		.paging-navigation a:hover {\r\n			border-color: #ff3131;\r\n			background-color: #ff3131;\r\n		}\r\n\r\n		.entry-meta .tag-links a:hover:before {\r\n			border-right-color: #ff3131;\r\n		}\r\n\r\n		.entry-meta .tag-links a:hover {\r\n			background-color: #ff3131;\r\n		}\r\n\r\n		@media screen and (min-width: 782px) {\r\n			.primary-navigation ul ul a:hover,\r\n			.primary-navigation ul ul li.focus > a {\r\n				background-color: #ff3131;\r\n			}\r\n		}\r\n\r\n		@media screen and (min-width: 1008px) {\r\n			.secondary-navigation ul ul a:hover,\r\n			.secondary-navigation ul ul li.focus > a {\r\n				background-color: #ff3131;\r\n			}\r\n		}\r\n\r\n		button:active,\r\n		.contributor-posts-link:active,\r\n		input[type="button"]:active,\r\n		input[type="reset"]:active,\r\n		input[type="submit"]:active,\r\n		.widget input[type="button"]:active,\r\n		.widget input[type="reset"]:active,\r\n		.widget input[type="submit"]:active,\r\n		.content-sidebar .widget input[type="button"]:active,\r\n		.content-sidebar .widget input[type="reset"]:active,\r\n		.content-sidebar .widget input[type="submit"]:active {\r\n			background-color: #ff4545;\r\n		}\r\n\r\n		.site-navigation .current_page_item > a,\r\n		.site-navigation .current_page_ancestor > a,\r\n		.site-navigation .current-menu-item > a,\r\n		.site-navigation .current-menu-ancestor > a {\r\n			color: #ff4545;\r\n		}\r\n	\r\n		/* Higher contrast Accent Color against contrast color */\r\n		.site-navigation .current_page_item > a,\r\n		.site-navigation .current_page_ancestor > a,\r\n		.site-navigation .current-menu-item > a,\r\n		.site-navigation .current-menu-ancestor > a,\r\n		.site-navigation a:hover,\r\n		.featured-content a:hover,\r\n		.featured-content .entry-title a:hover,\r\n		.widget a:hover,\r\n		.widget-title a:hover,\r\n		.widget_twentyfourteen_ephemera .entry-meta a:hover,\r\n		.hentry .mejs